import { useState } from "react";
import ProfessorGraph from "./Components/ProfessorGraph";
import "./App.css"

function App() {
  const [graphData, setGraphData] = useState({
    nodes: [{ id: "Professor Arti", group: 0, size: 50 }],
    links: []
  });
  const [newProfessor, setNewProfessor] = useState("");

  const addProfessor = () => {
    if (newProfessor.trim() === "") return;
    const newProf = { id: newProfessor, group: 1, size: 25 };
    setGraphData(prevData => ({
      nodes: [...prevData.nodes, newProf],
      links: [...prevData.links, { source: "Professor Arti", target: newProfessor, value: 2 }]
    }));
    setNewProfessor("");
  };

  return (
    <div style={{ display: "flex", height: "100vh" }}>
      <div style={{ width: "50%", padding: "20px", borderRight: "1px solid #ccc" }}>
        <h2>Add Professor</h2>
        <input
          type="text"
          value={newProfessor}
          onChange={(e) => setNewProfessor(e.target.value)}
          placeholder="Enter professor name"
        />
        <button onClick={addProfessor}>Add</button>
      </div>
      <div style={{ width: "50%", display: "flex", justifyContent: "center", alignItems: "center" }}>
        <ProfessorGraph data={graphData} />
      </div>
    </div>
  );
}

export default App;
