import React, { useState } from "react";
import axios from "axios";

const Chatbot = ({ professor }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");

    const sendMessage = async () => {
        if (!input.trim()) return;

        const userMessage = { text: input, sender: "user" };
        setMessages([...messages, userMessage]);

        try {
            const response = await axios.post("http://localhost:8000/chat/", new URLSearchParams({ user_input: input }));

            const botMessage = { text: response.data.response, sender: "bot" };
            setMessages([...messages, userMessage, botMessage]);
        } catch (error) {
            console.error("Error:", error);
        }

        setInput("");
    };

    return (

        <div class="chat-container">
            <h1>Chat with {professor}</h1>
            <div class="chat-box">
                {messages.map((msg, index) => {
                    <div key={index} className={msg.sender === "user" ? "user-message" : "bot-message"} >
                        <strong>{msg.sender}: </strong> {msg.text}
                    </div>
                })}
            </div>
            <input
                autocomplete="off"
                type="text"
                id="chat-input"
                placeholder={`Ask ${professor || "the professor"} a question...`}

            />
            <div class="buttons">
                <button id="send" onClick={sendMessage}>Send</button>
                <button id="clear" onClick={() => setMessages([])} >Clear</button>
            </div>
        </div>
    );
};

export default Chatbot;
