import React, { useState } from "react";
import axios from "axios";

const Chatbot = ({ professor }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");

    const sendMessage = async () => {
        if (!input.trim()) return;

        const userMessage = { text: input, sender: "user" };
        setMessages([...messages, userMessage]);

        try {
            const response = await axios.post("http://localhost:8000/chat/", new URLSearchParams({ user_input: input }));

            const botMessage = { text: response.data.response, sender: "bot" };
            setMessages([...messages, userMessage, botMessage]);
        } catch (error) {
            console.error("Error:", error);
        }

        setInput("");
    };

    return (

        <div class="chat-container">
            <div class="chat-box" id="chat-box"></div>
            <input autocomplete="off" type="text" id="chat-input" placeholder="Ask {professor name } a question" />
            <div class="buttons">
                <div class="send-clear">
                    <button id="send-btn">Send</button>
                    <button id="clear-btn">Clear</button>
                </div>
                <button id="recreate">Recreate</button>
            </div>
        </div>
    );
};

export default Chatbot;
