import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import ProfessorGraph from "./Components/ProfessorGraph";
import "./App.css"

function App() {
  const [graphData, setGraphData] = useState(() => {
    const savedData = localStorage.getItem("graphData");
    return savedData ? JSON.parse(savedData) : {
      nodes: [{ id: "Professor Arti", group: 0, size: 75 }],
      links: []
    };
  });


  const [newProfessor, setNewProfessor] = useState("");

  useEffect(() => {
    localStorage.setItem("graphData", JSON.stringify(graphData));
  }, [graphData]);

  const addProfessor = () => {
    if (newProfessor.trim() === "") return;
    const newProf = { id: newProfessor, group: 1, size: 35 };
    setGraphData(prevData => ({
      nodes: [...prevData.nodes, newProf],
      links: [...prevData.links, { source: "Professor Arti", target: newProfessor, value: 2 }]
    }));
    setNewProfessor("");
  };

  return (
    <Router>
      <div className="main-container">
        <div className="data-input">
          <h2>Add Professor</h2>
          <input
            type="text"
            value={newProfessor}
            onChange={(e) => setNewProfessor(e.target.value)}
            placeholder="Enter professor name"
          />
          <button onClick={addProfessor}>Add</button>
        </div>
        <div className="graph">
          <ProfessorGraph data={graphData} setData={setGraphData} />
        </div>
      </div>
    </Router>

  );
}

export default App;
